mapper.update:
update express_order
set order_status = #{orderStatus}
where order_id = #{orderId}

mapper.select:
select (1)
from express_order


        and order_id = #{orderId}


        and user_id = #{userId}


        and pay_id = #{payId}


        and courier_id = #{courierId}


        and express_id = #{expressId}
